INSERT INTO cars (id, win_number, body, brand, color, description, drive, image, level,
                  mileage, model, power, price, status, transmission, year)
    VALUES ('13', 'VF7RD5FNABL000015', 'Внедорожник', 'Mazda', 'Красный', 'Характеристики стремительного паркетника от японской марки. С прокатом Mazda CX-5 в Москве у вас будет эффектный городской автомобиль. Он нацелен привлекать внимание, выглядеть дорого и роскошно, поэтому прекрасно справляется с возложенной задачей. Для его внешнего облика характерны фигурная радиаторная решётка, узкие фары, стремительный профиль с правильными пропорциями. В интерьере использовали кожу, ткань, вставки под алюминий и дерево, и всё это с очень качественной сборкой. Вы получаете уютный и продуманный до мелочей салон с арендой Мазды CX-5 в Москве. Говоря о компактности авто, его размеры такие: длина кузова – 4.5 м, ширина – 1.8 м, высота – 1.6 м, клиренс – 192 мм. Но эти параметры не влияют на просторность и вместительность салона.',
        'Полный', 'Mazda_CX-5.jpg', 'Бизнес', '60000', 'CX-5', '150', '5100', 'Свободна', 'Автоматическая',  '2017');

INSERT INTO cars (id, win_number, body, brand, color, description, drive, image, level,
                  mileage, model, power, price, status, transmission, year)
    VALUES ('14', 'VF7RD5FNABL000016', 'Внедорожник', 'Mercedes-Benz', 'Чёрный', 'Характеристики Mercedes-Benz G-Wagen 2017 года – надёжность и мощь Перед вами не просто легенда, а ещё и улучшенная модификация с усовершенствованными подвесками, функционалом и увеличенной технической производительностью. Несмотря на свои квадратные формы, авто не лишено особой брутальной элегантности, поэтому подходит многим жителям и гостям столицы. Аренду Mercedes-Benz G-class в Москве заказывают даже для свадеб и годовщин. Но «классический» не равно «старый» облик. Так, инженеры установили в эту модель ксеноновые фары, светодиодную обводку огней, рельефные, прочные бамперы.  Традиционно это внедорожник с компактными размерами: его длина равна 4.6 м, ширина – 1.7 м, высота – 1.9 м. Дорожный просвет составляет 205 мм. Интерьер внутри уже не такой спартанский и грубый, как дизайн снаружи. Сразу в глаза бросается мультируль с четырьмя спицами, а сзади него расположена аккуратная приборная панель с TFT-экраном маршрутного компьютера. С прокатом Mercedes-Benz G-Wagen 2017 в Москве у вас такие параметры:  V-образный, дизельный двигатель с шестью цилиндрами и турбонаддувом, с 3 л и мощностью 245 л.с.; 7-ступенчатая коробка передач; мощная и укрепленная рама с лестничной конструкцией; пружинная подвеска на продольных рычагах и тягой «Панара» по круговой схеме; руль с гидроусилителем; технология распределения момента и три блокировки дифференциалов. Роскошен автомобиль и внутри. У него кожаный салон с деревянными вставками, климат-контроль, мощная аудиосистема. Несмотря на свои размеры, места внутри хватает даже пассажирам сзади. Спереди установлены кресла с боковой поддержкой и подогревом, сзади – диван. Багажник небольшой, рассчитан на 480 л. На дороге внедорожник ведёт себя уверено – аренда Mercedes G-class в Москве подходит и для узких улочек столицы, и для поездки на активный отдых, и для туризма.',
        'Полный', 'Mersedes-Geletvagen.jpg', 'Premium', '45000', 'G-Класс', '250', '14500', 'Свободна', 'Автоматическая',  '2017');

